package in.co.rays.basic;

public class CharArINJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char[] ch = new char[4];
		ch[0] = 's';
		ch[1] = 'r';
		ch[2] = 'w';
		
		for(int i =0; i< ch.length; i++) {
			System.out.println(ch[i]);
		}
		System.out.println(ch.length);

	}

}
