public class SmallLestNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 10, b= 20;
		
			if(a<b) {
				System.out.println("mini" + a);
				
			} else {
				System.out.println("mini" + b);
				
				
			}
			if(a<b) {
				System.out.println("max"+ b);
				
		} else {
			System.out.println("max" + a);
		}
		

	}

}
